    @Override
    public boolean onCreateOptionsMenu(Menu menu) {
        MenuInflater inflater=getMenuInflater();
        inflater.inflate(R.menu.search_menuu,menu);
        inflater.inflate(R.menu.rightmenu,menu);
        return true;
    }
    @Override
    public boolean onOptionsItemSelected(@NonNull MenuItem item) {
        int id =item.getItemId();
        if(id==R.id.share_menu){
            Intent sharingIntent = new Intent(Intent.ACTION_SEND);
            sharingIntent.setType("text/plain");
            sharingIntent.putExtra(android.content.Intent.EXTRA_TEXT, "Check out this cool application");
            sharingIntent.putExtra(android.content.Intent.EXTRA_SUBJECT, "Your application link here");
            startActivity(Intent.createChooser(sharingIntent, "Share Via"));
        }
        return true;
    }

    class Myadapter extends ArrayAdapter<String>{
        Context context;

        String mfristclubname[];
        String msecondclubname[];
        String mmatchesvs[];
        String mstadium[];
        String mdatetime[];
        int mfristclubpic[];
        int mseconclubpic[];
        Myadapter (Context c ,String fclubname[],String sclubname[]
                ,String matchvs[],String sstadium[],String ddatetime[],int fclubpic[], int sclubpic[]){
            super(c,R.layout.matches_one_row,R.id.fristclubname,fclubname);
            this.context=c;
            this.mfristclubname=fclubname;
            this.msecondclubname=sclubname;
            this.mmatchesvs=matchvs;
            this.mstadium=sstadium;
            this.mdatetime=ddatetime;
            this.mfristclubpic=fclubpic;
            this.mseconclubpic=sclubpic;


        }

        @NonNull
        @Override
        public View getView(int position, @Nullable View convertView, @NonNull ViewGroup parent) {
            LayoutInflater layoutInflater =(LayoutInflater) getApplicationContext().getSystemService(Context.LAYOUT_INFLATER_SERVICE);
            View row =layoutInflater.inflate(R.layout.matches_one_row,parent,false);
            TextView mfristn=row.findViewById(R.id.fristclubname);
            TextView msecondn=row.findViewById(R.id.secondclubname);
            TextView mvs=row.findViewById(R.id.vs_text);
            TextView mastadium=row.findViewById(R.id.match_stadium);
            TextView date=row.findViewById(R.id.matches_day_time);
            ImageView image1=row.findViewById(R.id.fristclubpicture);
            ImageView image2=row.findViewById(R.id.secondclubpicture);
            //setr resourse
            mfristn.setText(mfristclubname[position]);
            msecondn.setText(msecondclubname[position]);
            mvs.setText(mmatchesvs[position]);
            mastadium.setText(mstadium[position]);
            date.setText(mdatetime[position]);
            image1.setImageResource(mfristclubpic[position]);
            image2.setImageResource(mseconclubpic[position]);


            return row;
        }
    }
